CREATE TABLE users
(
  id         BIGINT AUTO_INCREMENT NOT NULL,
  username   VARCHAR(255)          NOT NULL UNIQUE,
  email      VARCHAR(255)          NOT NULL UNIQUE,
  password   VARCHAR(255)          NOT NULL,
  created_at datetime(3)           NULL,
  CONSTRAINT pk_users PRIMARY KEY (id)
);

