CREATE TABLE available_slots
(
  id          BIGINT AUTO_INCREMENT NOT NULL,
  start_time  datetime(3)           NULL,
  end_time    datetime(3)           NULL,
  is_reserved BIT(1)                NULL DEFAULT FALSE,
  CONSTRAINT pk_available_slots PRIMARY KEY (id)
);

